// src/components/ChatBot.js
import { useState } from "react";
import "../css/ChatBot.css";

export default function ChatBot() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([{ sender: "bot", text: "Hi ðŸ‘‹ How can I help you today?" }]);
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input };
    const botMessage = {
      sender: "bot",
      text: "ðŸ¤– AI response will go here (connect to backend later)",
    };

    setMessages((prev) => [...prev, userMessage, botMessage]);
    setInput("");
  };

  return (
    <>
      <div className="chatbot-button" onClick={() => setOpen(!open)}>
        ðŸ’¬
      </div>

      {open && (
        <div className="chatbot-window">
          <div className="chatbot-header">
            <span>KOOmind.ai ðŸ¤–</span>
            <button onClick={() => setOpen(false)}>âœ–</button>
          </div>

          <div className="chatbot-messages">
            {messages.map((msg, idx) => (
              <div key={idx} className={`chat-message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
          </div>

          <div className="chatbot-input">
            <input
              type="text"
              placeholder="Ask something..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleSend()}
            />
            <button onClick={handleSend}>âž¤</button>
          </div>
        </div>
      )}
    </>
  );
}
