  return (
    <>
      <button className="menu-toggle" onClick={() => setOpen(!open)}>
        {open ? "‚úñ" : "‚ò∞"}
      </button>

      <nav className={`menu-bar ${open ? "visible" : "hidden"}`}>
        <div className="menu-left" onClick={() => navigate("/dashboard")}>
          <span className="menu-logo">üß† KOOmind</span>
        </div>

        <ul className="menu-center">
          <li onClick={() => navigate("/dashboard")}>üè† Dashboard</li>
          <li onClick={() => navigate("/upload")}>üì§ Upload</li>
          <li onClick={() => navigate("/summary")}>üìë Summaries</li>
          <li onClick={() => navigate("/profile")}>üë§ Profile</li>
          <li onClick={() => navigate("/admin-panel")}>üõ†Ô∏è Admin Panel</li>
          <li onClick={() => navigate("/create-admin")}>‚ûï Create Admin</li>
        </ul>

        <div className="menu-right">
          <button className="menu-logout" onClick={handleLogout}>
            üîì Logout
          </button>
        </div>
      </nav>
    </>
  );
}
